import React from "react";
// nodejs library that concatenates classes
import classNames from "classnames";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";

// core components
import Header from "components/Header/Header.js";
import Footer from "components/Footer/Footer.js";
import GridContainer from "components/Grid/GridContainer.js";
import GridItem from "components/Grid/GridItem.js";
import HeaderLinks from "components/Header/HeaderLinks.js";
import Parallax from "components/Parallax/Parallax.js";

import styles from "assets/jss/material-kit-react/views/adminPage/adminStyle.js";
import useAxios from "axios-hooks";

import { Form, Formik, useField } from "formik";
import * as Yup from "yup";
//import classes from "*.module.css";

const useStyles = makeStyles(styles);

export default function QuizPage(props) {
  const classes = useStyles();
  const { ...rest } = props;

  ////Import Questions/////
  const url = "http://localhost:5000/questions";
  let [{ questions, loading, error, response }] = useAxios(url);

  if (loading) return <h5>Loading...</h5>;
  if (error)
    return (
      <h5 style={{ color: "red" }}>There was an error loading the admin</h5>
    );

  if (response.data) {
    questions = response.data;
  }



  return (
    <div>
      <Header
        color="transparent"
        brand="NoMoreBad.Gifts"
        rightLinks={<HeaderLinks />}
        fixed
        changeColorOnScroll={{
          height: 200,
          color: "dark",
        }}
        {...rest}
      />
      <Parallax small filter image={require("assets/img/gift.jpg")} />
      <div className={classNames(classes.main, classes.mainRaised)}>
        <div>
          <div className={classes.container}>
            <GridContainer justify="center">
              <GridItem xs={12} sm={12} md={8}>
                <div className={classes.profile}>
                  <div>
                    <h1>Admin Page</h1>
                  </div>
                </div>
              </GridItem>
            </GridContainer>
            <GridContainer justify="center">
              <GridItem xs={12} sm={12} md={6}>
                <h3>Quiz Questions</h3>
                <table className={classes.table}>
                  <thead>
                    <tr className={classes.firstRow}>
                      <th className={classes.header}>#</th>
                      <th className={classes.header}>Text</th>
                      <th className={classes.header}>Cat</th>
                    </tr>
                  </thead>
                  <tbody>
                    {Object.values(questions).map((question, id) => (
                      <tr key={id} className={classes.tableRow}>
                        <td className={classes.td}>
                          {question.questionNumber}
                        </td>
                        <td className={classes.td}>{question.text}</td>
                        <td className={classes.td}>{question.category}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </GridItem>
              <GridItem xs={12} sm={12} md={8}>
                <Formik
                  initialValues={{
                    questionNumber: 0,
                    text: "",
                    category: "",
                  }}
                  validationSchema={Yup.object({
                    //questionNumber: Yup.number(),
                      //.min(1, "Choose a valid question number"),
                      //.required("Required"),
                    text: Yup.string(),//.required("Required"),
                    //category: Yup.string()
                    //  .min(2, "Must be two characters")
                    //  .max(2, "Must be two characters"),
                  })}
                  onSubmit={(values, { setSubmitting, resetForm }) => {
                    console.log("onSubmit", values)
                    setTimeout(() => {
                      alert(JSON.stringify(values, null, 2));
                      resetForm();
                      setSubmitting(false);
                    }, 3000);
                  }}
                >
                  {(props) => (
                    <Form>
                      <h4>Add New Question</h4>
                      <CustomTextInput
                        label="Question Text"
                        name="text"
                        type="text"
                        placeholder="Enter question here..."
                      />
                      <button type="submit">
                        {props.isSubmitting ? "Loading..." : "Submit"}
                      </button>
                    </Form>
                  )}
                </Formik>
              </GridItem>
            </GridContainer>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
}

  ///Custom Components///
  const CustomTextInput = ({ label, ...props }) => {
    const [field, meta] = useField(props);

    return (
      <>
        <label htmlFor={props.id || props.name}>{label}</label>
        <input className={classes.textInput} {...field} {...props} />

        {meta.touched && meta.error ? (
          <div className={classes.error}>{meta.error}</div>
        ) : null}
      </>
    );
  };
